using Blazored.LocalStorage;
using Microsoft.AspNetCore.Components.Web;
using Microsoft.AspNetCore.Components.WebAssembly.Hosting;
using System;
using System.Net.Http.Json;
using Web;
using Web.Common;
using Web.Services;

var builder = WebAssemblyHostBuilder.CreateDefault(args);
builder.RootComponents.Add<App>("#app");
builder.RootComponents.Add<HeadOutlet>("head::after");

builder.Services.AddBlazoredLocalStorage();

var environment = builder.HostEnvironment.Environment;
var httpClient = new HttpClient { BaseAddress = new Uri(builder.HostEnvironment.BaseAddress) };
var config = await httpClient.GetFromJsonAsync<AppSettings>($"appsettings.{environment}.json");

if (config.ApiHost is null)
    throw new InvalidOperationException("apiBaseUrl (AppSettings:ApiHost) cannot be null, please update your appSettings.json configuration.");

builder.Services.AddScoped(sp => new HttpClient { BaseAddress = new Uri(config.ApiHost) });
builder.Services.AddScoped<IAuthValidator, AuthValidator>();
builder.Services.AddScoped<IAccountService, AccountService>();
builder.Services.AddScoped<ILinkService, LinkService>();

builder.Services.AddSingleton<AccountState>();

await builder.Build().RunAsync();

using Blazored.LocalStorage;
using Microsoft.AspNetCore.Components.Web;
using Microsoft.AspNetCore.Components.WebAssembly.Hosting;
using System;
using System.Net.Http.Json;
using Web;
using Web.Common;
using Web.Services;

var builder = WebAssemblyHostBuilder.CreateDefault(args);
builder.RootComponents.Add<App>("#app");
builder.RootComponents.Add<HeadOutlet>("head::after");

builder.Services.AddBlazoredLocalStorage();

var environment = builder.HostEnvironment.Environment;
var httpClient = new HttpClient { BaseAddress = new Uri(builder.HostEnvironment.BaseAddress) };
var config = await httpClient.GetFromJsonAsync<AppSettings>($"appsettings.{environment}.json");

// Validation de la configuration des hôtes API
if (config?.ApiHosts == null || !config.ApiHosts.Any())
{
    // Fallback vers l'ancien système si disponible
    if (!string.IsNullOrEmpty(config?.ApiHost))
    {
        config.ApiHosts = new List<Shared.DTOs.ApiHostDto>
        {
            new() { Name = "Default", Url = config.ApiHost }
        };
    }
    else
    {
        throw new InvalidOperationException("ApiHosts cannot be null or empty, please update your appSettings.json configuration.");
    }
}

// Utiliser le premier hôte API pour le HttpClient par défaut
var defaultApiHost = config.ApiHosts.First().Url;
builder.Services.AddScoped(sp => new HttpClient { BaseAddress = new Uri(defaultApiHost) });

// Enregistrer les services
builder.Services.AddScoped<IAuthValidator, AuthValidator>();
builder.Services.AddScoped<IAccountService, AccountService>();
builder.Services.AddScoped<ILinkService, LinkService>();
builder.Services.AddScoped<IApiHostService, ApiHostService>();

// Enregistrer la configuration comme singleton pour l'injection
builder.Services.AddSingleton(config);

builder.Services.AddSingleton<AccountState>();

await builder.Build().RunAsync();

using Shared.DTOs;

namespace Web.Services
{
    public interface IApiHostService
    {
        List<ApiHostDto> GetApiHosts();
        string GetApiHostUrl(string hostName);
    }

    public class ApiHostService : IApiHostService
    {
        private readonly IConfiguration _config;

        public ApiHostService(IConfiguration config)
        {
            _config = config;
        }

        public List<ApiHostDto> GetApiHosts()
        {
            var apiHosts = _config.GetSection("ApiHosts").Get<List<ApiHostDto>>() ?? new List<ApiHostDto>();
            return apiHosts;
        }

        public string GetApiHostUrl(string hostName)
        {
            var apiHosts = GetApiHosts();
            var selectedHost = apiHosts.FirstOrDefault(h => h.Name == hostName);
            return selectedHost?.Url ?? throw new ArgumentException($"API Host '{hostName}' not found.");
        }
    }
}

